package week1.day2;

public class NegativeToPositiveAssignment1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int negative= -20;

if(negative>0) {
	System.out.println(negative + " is Positive number");
}
else {
	System.out.println(negative + " is Negative number");
negative = negative * -1;
	}
System.out.println(negative + " is Positive number");
	}
}
